import React, { useEffect, useState } from "react";
import { View, Text, Button, SafeAreaView } from "react-native";
import styled from "styled-components/native";

const Page = styled.SafeAreaView`
  flex: 1;
  align-items: center;
`;

const HeaderText = styled.Text`
  font-size: 25px;
`;

const Input = styled.TextInput`
  width: 90%;
  height: 50px;
  font-size: 18px;
  background-color: #EEE;
  margin-top:20px;
  border-radius:10px;
  padding: 10px;
`;

const CalcButton = styled.Button`
  margin-top: 10px;
`;

const ResultArea = styled.View`
width:100%;
margin-top:30px;
background-color:#EEE;
padding:20px;
justify-content:center;
align-items:center;
`;

const ResultItemTitle = styled.Text`
font-size:18px;
font-weight:bold;
`;

const ResultItem = styled.Text`
font-size:15px;
margin-bottom:30px;
`;

const  PctArea =styled.View`
flex-direction:row;
margin:20px;
`;

const PctItem = styled.Button``;
export default () => {

  const [bill, setBill] = useState('');
  const [tip,setTip] = useState(0);
  const [pct, setPct] = useState(10);
  const [total,setTotal] = useState(0);

  const calc = () => {
    let nBill = parseFloat(bill); 

    if(nBill) {
      setTip ( (pct/100) * nBill );
    }
  }
const calcTotal = () => {
  let tot = bill + tip;
  setTotal(tot);
}

  useEffect(()=>{
    calc();

  }, [pct]);




  return(
    <Page>
      <HeaderText>Calculadora de Gorjeta</HeaderText>

      <Input
        placeholder="Quanto deu a conta?"
        placeholderTextColor="#000"
        keyboardType="numeric"
        value={bill}
        onChangeText={n=>setBill(n)}
      />


        <PctArea>
          <PctItem title="5%" onPress={() => setPct(5)}/>
          <PctItem title="10%" onPress={() => setPct(10)}/>
          <PctItem title="15%" onPress={() => setPct(15)}/>
          <PctItem title="20%" onPress={() => setPct(20)}/> 
        </PctArea>

      <CalcButton title={`Calcular ${pct}%`} onPress={calcTotal} />

      {tip > 0 &&
      <ResultArea>
        <resultItemTitle>Valor da Conta</resultItemTitle>
        <ResultItem>R$ {parseFloat(bill).toFixed(2)}</ResultItem>

        <resultItemTitle>Valor da Gorjeta</resultItemTitle>
        <ResultItem>R$ {tip.toFixed(2)} ({pct}%)</ResultItem>

       {total > 0 &&
        <>
        <resultItemTitle>Valor Total</resultItemTitle>
        <ResultItem>R$ {(parseFloat(bill)+tip).toFixed(2)}</ResultItem>
        </>
        }
        </ResultArea>

      }

    </Page>
  );
}
